import axios from "axios";
import React, { useRef, useState } from "react";
import lodash from "lodash";
// https://hn.algolia.com/api/v1/search?query=react
const HackerNews = () => {
  const [hits, setHits] = useState([]);
  const [query, setQuery] = useState("react");
  const [loading, setLoading] = useState(true);
  const [errorMessage, setErrorMessage] = useState("");
  const handleFetchdata = useRef({});
  handleFetchdata.current = async () => {
    setLoading(true);
    try {
      const response = await axios.get(
        `https://hn.algolia.com/api/v1/search?query=${query}`
      );
      setHits(response.data?.hits || []);
      setLoading(false);
    } catch (error) {
      setLoading(false);
      setErrorMessage(`the error message is ${error}`);
    }
  };
  const handleUpdateQuery = lodash.debounce((e) =>{
    setQuery(e.target.value)
  },500)
  React.useEffect(() => {
    handleFetchdata.current();
  }, [query]);
  return (
    <div className="bg-white ">
      <input
        type="text"
        className="border border-green-500 text-black p-5 mb-5"
        defaultValuevalue={query}
        onChange={handleUpdateQuery}
      />
      {loading && (
        <p className="loading w-8 h-8 rounded-full  border-blue-500 border-4 border-r-4 border-r-transparent animate-spin"></p>
      )}
      {!loading && errorMessage && <p>{errorMessage}</p>}
      {!loading &&
        hits.length > 0 &&
        hits.map((item, index) => <h3 key={item.title}>{item.title}</h3>)}
    </div>
  );
};

export default HackerNews;
