import axios from "axios";
import React, { useRef, useState } from "react";
// https://hn.algolia.com/api/v1/search?query=react
const HackerNews = () => {
  const [hits, setHits] = useState([]);
  const [query, setQuery] = useState("react");
  const [loading, setLoading] = useState(true);
  const handleFetchdata = useRef({});
  handleFetchdata.current = async () => {
    setLoading(true);
    const response = await axios.get(
      `https://hn.algolia.com/api/v1/search?query=${query}`
    );
    // console.log(response.data);
    // if (response.data) {
    //   console.log(response.data.hits);
    // }
    setHits(response.data?.hits || []);
    setLoading(false);
  };
  React.useEffect(() => {
    handleFetchdata.current();
  }, [query]);
  return (
    <div>
      <input
        type="text"
        className="border border-green-500 text-black p-5 mb-5"
        value={query}
        onChange={(e) => setQuery(e.target.value)}
      />
      {loading && <p className="loading w-8 h-8 rounded-full border border-blue-500 border-4 border">Loading...</p>}
      {!loading && hits.length > 0 &&
        hits.map((item, index) => <h3 key={item.title}>{item.title}</h3>)}
    </div>
  );
};

export default HackerNews;
